                        var userName = $(this).parent('li').data('value');
                        var atCh = cm.getRange({line: curLine, ch: 0}, {line: curLine, ch: curCh}).lastIndexOf('@');    //最后一个@的位置
                        cm.replaceRange(userName + ' ', {line: curLine, ch: atCh + 1}, {line: curLine, ch: curCh});
